      function show_runtime() {

          window.setTimeout("show_runtime()", 1000);

          X = new Date("2024/01/01 17:00:00"); // 

          Y = new Date(); // 

          T = (Y.getTime() - X.getTime()); // 

          M = 24 * 60 * 60 * 1000; // 

          a = T / M; // 

          A = Math.floor(a); // 

          b = (a - A) * 24; // 

          B = Math.floor(b); // 

          c = (b - B) * 60; // 

          C = Math.floor((b - B) * 60); // 

          D = Math.floor((c - C) * 60); // 

          runtime_span.innerHTML = " <span class='runtime'>" + A + "" + B + "" + C + "" + D + "</span>";

      }

      show_runtime();

      </script>
