function applyFontSetting() {    if (localStorage.getItem("LXGWFontEnabled") === "true") {        document.body.classList.add("LXGWMode");    } else {        document.body.classList.remove("LXGWMode");    }}
function toggleLXGWFont() {    var button = document.querySelector('.custom-button'); // Find the button    if (localStorage.getItem("LXGWFontEnabled") === "true") {        localStorage.setItem("LXGWFontEnabled", "false");        document.body.classList.remove("LXGWMode");        button.innerHTML = '<img src="https://cdn.jsdelivr.net/gh/infinitesum/Twikoo-emoji@master/Blob/ablobcatrainbow.png" alt="Emoji" style="vertical-align: middle; width: 20px; height: 20px;"> ';    } else {        localStorage.setItem("LXGWFontEnabled", "true");        document.body.classList.add("LXGWMode");        button.innerHTML = '<img src="https://cdn.jsdelivr.net/gh/infinitesum/Twikoo-emoji@master/Blob/ablobcatrainbow.png" alt="Emoji" style="vertical-align: middle; width: 20px; height: 20px;"> ';    }}
function updateButtonText() {    var button = document.querySelector('.custom-button'); // Find the button    if (localStorage.getItem("LXGWFontEnabled") === "true") {        button.innerHTML = '<img src="https://cdn.jsdelivr.net/gh/infinitesum/Twikoo-emoji@master/Blob/ablobcatrainbow.png" alt="Emoji" style="vertical-align: middle; width: 20px; height: 20px;"> ';    } else {        button.innerHTML = '<img src="https://cdn.jsdelivr.net/gh/infinitesum/Twikoo-emoji@master/Blob/ablobcatrainbow.png" alt="Emoji" style="vertical-align: middle; width: 20px; height: 20px;"> ';    }}
