name: auto deploy
on:  workflow_dispatch:  push:
jobs:  build:    runs-on: ubuntu-latest #  Ubuntu    name: auto deploy    steps:    # 1.     - name: Checkout      uses: actions/checkout@v4 #  actions/checkout@v3      with: #         submodules: true # Checkout private submodules(themes or something else).         fetch-depth: 0
    - name: Restore file modification time       run: find source/_posts -name '*.md' | while read file; do touch -d "$(git log -1 --format="@%ct" "$file")" "$file"; done
    - name: Restore file modification time of wiki      run: find source/wiki -name '*.md' | while read file; do touch -d "$(git log -1 --format="@%ct" "$file")" "$file"; done
    - name: Restore file modification time of notes      run: find source/notes -name '*.md' | while read file; do touch -d "$(git log -1 --format="@%ct" "$file")" "$file"; done
    # 2.     - name: Setup Node.js 18.19.x      uses: actions/setup-node@master      with:        node-version: "18.19.x"
    - name: Install pandoc      run: |        cd /tmp        wget -c https://github.com/jgm/pandoc/releases/download/2.14.0.3/pandoc-2.14.0.3-1-amd64.deb        sudo dpkg -i pandoc-2.14.0.3-1-amd64.deb
    # 3.     - name: Generate Public Files      run: |        npm i        npm install hexo-cli -g        hexo clean && hexo generate    # 4a.  GitHub     - name: Deploy to GitHub Pages      uses: peaceiris/actions-gh-pages@v3      with:        deploy_key: ${{ secrets.HEXO_DEPLOY_PRI }} #         external_repository: # GitHub pages        publish_branch: gt-pages #         publish_dir: ./public        commit_message: ${{ github.event.head_commit.message }}        user_name: 'github-actions[bot]'        user_email: 'github-actions[bot]@users.noreply.github.com'
